export const WelcomeSection = () => {
  const buttons = [
    {
      id: 1,
      text: "Our Services",
      variant: "primary",
      bgClass: "bg-white",
      textClass: "text-black",
      borderClass: "",
      left: "27px",
    },
    {
      id: 2,
      text: "Contact Us",
      variant: "secondary",
      bgClass: "",
      textClass: "text-white",
      borderClass: "border-2 border-solid border-white",
      left: "295px",
    },
  ];

  return (
    <section className="absolute top-[253px] left-14 w-[892px] h-[564px]">
      <div className="absolute top-0 left-0 w-[332px] h-[57px]">
        <div className="absolute top-0 left-0 w-[330px] h-[57px] bg-[#ffffff1a] rounded-[70px] border-[3px] border-solid border-[#ffffff26] backdrop-blur-[24.9px] backdrop-brightness-[100%] [-webkit-backdrop-filter:blur(24.9px)_brightness(100%)]" />
        
        <div className="absolute w-[22px] h-[26px] top-[15.5px] left-[28px] bg-white rounded flex items-center justify-center">
          <span className="text-black text-xs">⚙</span>
        </div>
        
        <p className="absolute top-[19px] left-[65px] font-normal text-white text-[25px] tracking-[0] leading-5 whitespace-nowrap">
          Industrial Reliability
        </p>
      </div>

      <h1 className="absolute top-[90px] left-0 w-[884px] font-extrabold text-transparent text-[80.9px] tracking-[0] leading-[82.4px]">
        <span className="text-white">Engineering Precision for </span>
        <span className="text-[#fbcc00]">Critical</span>
        <span className="text-white">&nbsp;</span>
        <span className="text-[#fbcc00]">Industrial</span>
        <span className="text-white"> Systems</span>
      </h1>

      <p className="absolute top-[358px] left-0 w-[857px] font-medium text-[#ffffff99] text-[25px] tracking-[0] leading-[34.0px]">
        We design and deliver reliable auxiliary systems for oil & gas,
        power, and process industries—ensuring efficiency, safety, and
        uninterrupted operations across every stage of your plant.
      </p>

      <div className="absolute top-[504px] left-0 flex gap-[30px]">
        {buttons.map((button, index) => (
          <button
            key={button.id}
            className={`w-[239px] h-[60px] rounded-[74.22px] ${button.bgClass} ${button.borderClass} flex items-center justify-center font-medium text-[25px] text-center tracking-[0] leading-[31.5px] ${button.textClass} transition-all duration-300 hover:opacity-90 focus:outline-none focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-transparent`}
            aria-label={button.text}
          >
            {button.text}
          </button>
        ))}
      </div>
    </section>
  );
};
