import { ClientsSection } from "./ClientsSection";
import { FeaturedProductsSection } from "./FeaturedProductsSection";
import { FooterSection } from "./FooterSection";
import { HeaderSection } from "./HeaderSection";
import { WelcomeSection } from "./WelcomeSection";
import { images } from "../config/images";

export const Homepage = () => {
  const featuredProducts = [
    {
      id: 1,
      title: "Dosing System",
      image: images.dosingSystem,
    },
    {
      id: 2,
      title: "Seal auxiliary systems",
      image: images.sealAuxiliary,
    },
    {
      id: 3,
      title: "Control & Instrumentation",
      image: images.controlInstrumentation,
    },
    {
      id: 4,
      title: "Lube Oil Systems",
      image: images.lubeOilSystems,
    },
    {
      id: 5,
      title: "Fuel Oil Systems",
      image: images.fuelOilSystems,
    },
  ];

  return (
    <div className="bg-white w-full">
      {/* Hero Section with Background */}
      <div 
        className="relative h-screen"
        style={{
          backgroundImage: `url(${images.heroBackground})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat'
        }}
      >
        <HeaderSection />
        <WelcomeSection />
      </div>
      
      {/* Main Content Sections */}
      <FeaturedProductsSection />
      
      {/* Featured Products Section */}
      <section className="py-16 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          {/* Featured Products Title */}
          <h2 className="text-left text-4xl sm:text-5xl lg:text-6xl font-fustat font-extrabold mb-8">
            <span className="text-black">Featured </span>
            <span className="text-[#fbcc00]">Products</span>
          </h2>

          {/* Divider Line */}
          <div className="w-full h-0.5 bg-gray-300 mb-12" />

          {/* Products Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-16">
            {featuredProducts.slice(0, 3).map((product) => (
              <div key={product.id} className="flex flex-col items-center">
                <div className="w-full aspect-square rounded-lg overflow-hidden mb-4 hover:scale-105 transition-transform duration-300">
                  <img 
                    src={product.image} 
                    alt={product.title}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 bg-[#fbcc00] rounded-lg flex-shrink-0" />
                  <span className="font-semibold text-gray-800 text-lg">{product.title}</span>
                </div>
              </div>
            ))}
          </div>

          {/* Second Row - Centered */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 max-w-4xl mx-auto">
            {featuredProducts.slice(3).map((product) => (
              <div key={product.id} className="flex flex-col items-center">
                <div className="w-full aspect-square rounded-lg overflow-hidden mb-4 hover:scale-105 transition-transform duration-300">
                  <img 
                    src={product.image} 
                    alt={product.title}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 bg-[#fbcc00] rounded-lg flex-shrink-0" />
                  <span className="font-semibold text-gray-800 text-lg">{product.title}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Clients Section with Yellow Background */}
      <section className="bg-[#eec755] py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <ClientsSection />
            
            {/* Industrial Equipment Image */}
            <div className="w-full aspect-[4/3] rounded-lg overflow-hidden">
              <img 
                src={images.industrialEquipment} 
                alt="Industrial Equipment"
                className="w-full h-full object-cover"
              />
            </div>
          </div>

          {/* Client Logos Grid */}
          <div className="mt-16">
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {[
                { id: 1, image: images.clientLogo1, alt: "Client 1" },
                { id: 2, image: images.clientLogo2, alt: "Client 2" },
                { id: 3, image: images.clientLogo3, alt: "Client 3" },
                { id: 4, image: images.clientLogo4, alt: "Client 4" },
              ].map((client) => (
                <div
                  key={client.id}
                  className="bg-white rounded-2xl p-8 shadow-lg flex items-center justify-center aspect-[2/1] overflow-hidden"
                >
                  <img 
                    src={client.image} 
                    alt={client.alt}
                    className="max-w-full max-h-full object-contain"
                  />
                </div>
              ))}
            </div>
          </div>

          {/* Dots Indicator */}
          <div className="flex justify-center mt-8">
            <div className="flex space-x-2">
              {[1, 2, 3, 4].map((dot) => (
                <div key={dot} className="w-3 h-3 bg-gray-400 rounded-full" />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Footer Section */}
      <footer className="bg-[#0e0e0e] text-white py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-12">
            {/* Company Info */}
            <div className="lg:col-span-1">
              <div className="w-full max-w-sm h-28 rounded flex items-center justify-center mb-6 overflow-hidden">
                <img 
                  src={images.companyLogo} 
                  alt="PSI Engineering Systems"
                  className="max-w-full max-h-full object-contain"
                />
              </div>
              <p className="text-gray-400 text-lg leading-relaxed mb-8">
                We design and deliver reliable auxiliary systems for oil & gas,
                power, and process industries—ensuring efficiency, safety, and
                uninterrupted operations across every stage of your plant.
              </p>
              
              {/* Social Icons */}
              <div className="flex space-x-4">
                {[
                  { name: 'Facebook', image: images.socialFacebook },
                  { name: 'Twitter', image: images.socialTwitter },
                  { name: 'LinkedIn', image: images.socialLinkedIn },
                  { name: 'YouTube', image: images.socialYouTube },
                ].map((social) => (
                  <div key={social.name} className="w-12 h-12 rounded-full overflow-hidden hover:scale-110 transition-transform cursor-pointer">
                    <img 
                      src={social.image} 
                      alt={social.name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                ))}
              </div>
            </div>

            {/* Footer Navigation */}
            <div className="lg:col-span-2">
              <FooterSection />
            </div>
          </div>

          {/* Copyright */}
          <div className="border-t border-gray-600 mt-12 pt-8">
            <p className="text-center text-gray-400 text-lg">
              © Copyright ©2025 PSI. All Rights Reserved Copyright
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};
