export const WelcomeSection = () => {
  const buttons = [
    {
      id: 1,
      text: "Our Services",
      variant: "primary",
      bgClass: "bg-white hover:bg-gray-100",
      textClass: "text-black",
      borderClass: "",
    },
    {
      id: 2,
      text: "Contact Us",
      variant: "secondary",
      bgClass: "bg-transparent hover:bg-white/10",
      textClass: "text-white",
      borderClass: "border-2 border-white",
    },
  ];

  return (
    <section className="flex items-center justify-center min-h-screen px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="max-w-4xl">
          {/* Industrial Reliability Badge */}
          <div className="inline-flex items-center bg-white/10 backdrop-blur-md rounded-full px-6 py-3 border border-white/20 mb-8">
            <div className="w-6 h-6 bg-white rounded flex items-center justify-center mr-3 flex-shrink-0">
              <span className="text-black text-sm">⚙</span>
            </div>
            <span className="text-white text-lg sm:text-xl font-medium">
              Industrial Reliability
            </span>
          </div>

          {/* Main Heading */}
          <h1 className="text-4xl sm:text-5xl lg:text-6xl xl:text-7xl font-extrabold leading-tight mb-8">
            <span className="text-white">Engineering Precision for </span>
            <span className="text-[#fbcc00]">Critical</span>
            <span className="text-white"> </span>
            <span className="text-[#fbcc00]">Industrial</span>
            <span className="text-white"> Systems</span>
          </h1>

          {/* Description */}
          <p className="text-lg sm:text-xl lg:text-2xl text-gray-300 leading-relaxed mb-12 max-w-3xl">
            We design and deliver reliable auxiliary systems for oil & gas,
            power, and process industries—ensuring efficiency, safety, and
            uninterrupted operations across every stage of your plant.
          </p>

          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 sm:gap-6">
            {buttons.map((button) => (
              <button
                key={button.id}
                className={`px-8 py-4 rounded-full font-medium text-lg sm:text-xl transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-transparent ${button.bgClass} ${button.textClass} ${button.borderClass}`}
                aria-label={button.text}
              >
                {button.text}
              </button>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};
