import { images } from "../config/images";

export const HeaderSection = () => {
  const navigationItems = [
    { label: "Home" },
    { label: "About Us" },
    { label: "Products" },
    { label: "Clientele" },
    { label: "Contact Us" },
  ];

  return (
    <header className="absolute top-0 left-0 right-0 z-10 px-2 sm:px-4 lg:px-6 py-3">
      <div className="max-w-full mx-auto">
        <div className="flex items-center justify-between">
          {/* Logo - Moved further left */}
          <div className="flex-shrink-0 mr-8">
            <img 
              src={images.companyLogo} 
              alt="PSI Engineering Systems"
              className="h-12 sm:h-14 lg:h-16 w-auto object-contain"
            />
          </div>

          {/* Desktop Navigation - Made longer and more centered */}
          <div className="hidden lg:flex items-center justify-center flex-1 max-w-4xl mx-auto">
            <div className="flex items-center space-x-8 bg-white/15 backdrop-blur-md rounded-full px-12 py-2.5 border border-white/25 shadow-lg">
              <nav className="flex space-x-8" role="navigation" aria-label="Main navigation">
                {navigationItems.map((item, index) => (
                  <a
                    key={index}
                    href={`#${item.label.toLowerCase().replace(/\s+/g, "-")}`}
                    className="text-white font-medium text-sm hover:text-[#fbcc00] transition-colors duration-300 whitespace-nowrap"
                  >
                    {item.label}
                  </a>
                ))}
              </nav>
              
              {/* Login Button - Reduced distance from nav items */}
              <a
                href="#login"
                className="bg-[#fbcc00] hover:bg-[#e6b800] text-black font-semibold px-5 py-2 rounded-full transition-colors duration-300 ml-3 text-sm whitespace-nowrap"
                aria-label="Login to your account"
              >
                Login
              </a>
            </div>
          </div>

          {/* Right spacer to balance the layout */}
          <div className="hidden lg:block w-24"></div>

          {/* Mobile Menu Button */}
          <button className="lg:hidden text-white p-2">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>

        {/* Mobile Navigation */}
        <div className="lg:hidden mt-3 bg-white/15 backdrop-blur-md rounded-2xl p-3 border border-white/25">
          <nav className="flex flex-col space-y-2" role="navigation" aria-label="Mobile navigation">
            {navigationItems.map((item, index) => (
              <a
                key={index}
                href={`#${item.label.toLowerCase().replace(/\s+/g, "-")}`}
                className="text-white font-medium text-sm hover:text-[#fbcc00] transition-colors duration-300 py-1.5"
              >
                {item.label}
              </a>
            ))}
            {/* Mobile Login Button */}
            <a
              href="#login"
              className="bg-[#fbcc00] hover:bg-[#e6b800] text-black font-semibold px-5 py-2.5 rounded-full transition-colors duration-300 text-center mt-3 text-sm"
              aria-label="Login to your account"
            >
              Login
            </a>
          </nav>
        </div>
      </div>
    </header>
  );
};
