import { images } from "../config/images";

export const HeaderSection = () => {
  const navigationItems = [
    { label: "Home" },
    { label: "About Us" },
    { label: "Products" },
    { label: "Clientele" },
    { label: "Contact Us" },
  ];

  return (
    <header className="relative z-10 px-4 sm:px-6 lg:px-8 py-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <div className="flex-shrink-0">
            <div className="w-48 sm:w-64 lg:w-80 h-16 sm:h-20 lg:h-24 rounded flex items-center justify-center overflow-hidden">
              <img 
                src={images.companyLogo} 
                alt="PSI Engineering Systems"
                className="max-w-full max-h-full object-contain"
              />
            </div>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden lg:flex items-center space-x-8 bg-white/10 backdrop-blur-md rounded-full px-8 py-3 border border-white/20">
            <nav className="flex space-x-8" role="navigation" aria-label="Main navigation">
              {navigationItems.map((item, index) => (
                <a
                  key={index}
                  href={`#${item.label.toLowerCase().replace(/\s+/g, "-")}`}
                  className="text-white font-semibold text-lg hover:text-[#fbcc00] transition-colors duration-300"
                >
                  {item.label}
                </a>
              ))}
            </nav>
          </div>

          {/* Login Button & Mobile Menu */}
          <div className="flex items-center space-x-4">
            <a
              href="#login"
              className="bg-[#fbcc00] hover:bg-[#e6b800] text-black font-semibold px-6 py-3 rounded-full transition-colors duration-300"
              aria-label="Login to your account"
            >
              Login
            </a>

            {/* Mobile Menu Button */}
            <button className="lg:hidden text-white p-2">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        <div className="lg:hidden mt-4 bg-white/10 backdrop-blur-md rounded-2xl p-4 border border-white/20">
          <nav className="flex flex-col space-y-3" role="navigation" aria-label="Mobile navigation">
            {navigationItems.map((item, index) => (
              <a
                key={index}
                href={`#${item.label.toLowerCase().replace(/\s+/g, "-")}`}
                className="text-white font-semibold text-lg hover:text-[#fbcc00] transition-colors duration-300 py-2"
              >
                {item.label}
              </a>
            ))}
          </nav>
        </div>
      </div>
    </header>
  );
};
