import { useState, useEffect } from 'react';
import { images } from '../config/images';

export const ClientCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  const clients = [
    { id: 1, image: images.clientAlstom, alt: "Alstom" },
    { id: 2, image: images.clientBASF, alt: "BASF" },
    { id: 3, image: images.clientChevron, alt: "Chevron" },
    { id: 4, image: images.clientDoosan, alt: "Doosan" },
    { id: 5, image: images.clientEDF, alt: "EDF" },
    { id: 6, image: images.clientFlowserve, alt: "Flowserve" },
    { id: 7, image: images.clientKCP, alt: "KCP" },
    { id: 8, image: images.clientLarsenTurbo, alt: "Larsen & Turbo" },
    { id: 9, image: images.clientMHI, alt: "MHI" },
    { id: 10, image: images.clientPetronas, alt: "Petronas" },
    { id: 11, image: images.clientSiemens, alt: "Siemens" },
    { id: 12, image: images.clientSolenis, alt: "Solenis" },
    { id: 13, image: images.clientSRC, alt: "SRC" },
    { id: 14, image: images.clientTATA, alt: "TATA" },
    { id: 15, image: images.clientToyo, alt: "Toyo" },
    { id: 16, image: images.clientUR, alt: "UR" },
  ];

  // Auto-scroll functionality
  useEffect(() => {
    if (!isAutoPlaying) return;

    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % clients.length);
    }, 3000); // Change slide every 3 seconds

    return () => clearInterval(interval);
  }, [isAutoPlaying, clients.length]);

  const goToPrevious = () => {
    setIsAutoPlaying(false);
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? clients.length - 1 : prevIndex - 1
    );
  };

  const goToNext = () => {
    setIsAutoPlaying(false);
    setCurrentIndex((prevIndex) => (prevIndex + 1) % clients.length);
  };

  const goToSlide = (index) => {
    setIsAutoPlaying(false);
    setCurrentIndex(index);
  };

  // Get visible slides (4 at a time)
  const getVisibleSlides = () => {
    const slides = [];
    for (let i = 0; i < 4; i++) {
      const index = (currentIndex + i) % clients.length;
      slides.push(clients[index]);
    }
    return slides;
  };

  return (
    <div className="relative">
      {/* Carousel Container */}
      <div className="overflow-hidden">
        <div className="flex gap-6 transition-transform duration-500 ease-in-out">
          {getVisibleSlides().map((client) => (
            <div
              key={client.id}
              className="bg-white rounded-2xl p-8 shadow-lg flex items-center justify-center flex-shrink-0 w-64 h-32"
            >
              <img 
                src={client.image} 
                alt={client.alt}
                className="max-w-full max-h-full object-contain"
              />
            </div>
          ))}
        </div>
      </div>

      {/* Navigation Arrows */}
      <button
        onClick={goToPrevious}
        className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-4 bg-white rounded-full p-3 shadow-lg hover:bg-gray-100 transition-colors z-10"
        aria-label="Previous slide"
      >
        <svg className="w-6 h-6 text-gray-800" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
      </button>

      <button
        onClick={goToNext}
        className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-4 bg-white rounded-full p-3 shadow-lg hover:bg-gray-100 transition-colors z-10"
        aria-label="Next slide"
      >
        <svg className="w-6 h-6 text-gray-800" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>
      </button>

      {/* Dots Indicator */}
      <div className="flex justify-center mt-8 gap-2">
        {clients.map((_, index) => (
          <button
            key={index}
            onClick={() => goToSlide(index)}
            className={`w-3 h-3 rounded-full transition-colors ${
              index === currentIndex ? 'bg-gray-700' : 'bg-gray-400'
            }`}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
};
